import React, { useState, useEffect, useReducer, useMemo } from "react";
import axios from "axios";
import {
  IoMdSunny,
  IoMdRainy,
  IoMdCloudy,
  IoMdSnow,
  IoMdThunderstorm,
  IoMdSearch,
} from "react-icons/io";
import {
  BsCloudHaze2Fill,
  BsCloudDrizzleFill,
  BsEye,
  BsThermometer,
  BsWind,
  BsDroplet,
} from "react-icons/bs";

// Reducer to manage all weather and UI state
const weatherReducer = (state, action) => {
  switch (action.type) {
    case "FETCH_WEATHER_START":
      return { ...state, loading: true, error: null, weatherData: null };
    case "FETCH_WEATHER_SUCCESS":
      return { ...state, loading: false, weatherData: action.payload };
    case "FETCH_WEATHER_ERROR":
      return { ...state, loading: false, error: action.payload };
    case "SET_COUNTRY":
      return { ...state, country: action.payload };
    case "SET_LOCATION":
      return { ...state, location: action.payload };
    default:
      return state;
  }
};

const App = () => {
  const [state, dispatch] = useReducer(weatherReducer, {
    location: "Pune",
    loading: false,
    weatherData: null,
    error: null,
    country: "",
  });

  const weatherDescriptions = useMemo(() => ({
    0: "Clear sky",
    1: "Mainly clear",
    2: "Partly cloudy",
    3: "Cloudy",
    45: "Fog",
    48: "Depositing rime fog",
    51: "Light drizzle",
    53: "Moderate drizzle",
    55: "Dense drizzle",
    56: "Light freezing drizzle",
    57: "Dense freezing drizzle",
    61: "Light rain",
    63: "Moderate rain",
    65: "Heavy rain",
    66: "Light freezing rain",
    67: "Heavy freezing rain",
    71: "Light snow",
    73: "Moderate snow",
    75: "Heavy snow",
    77: "Snow grains",
    80: "Showers of rain",
    81: "Heavy showers of rain",
    82: "Violent showers of rain",
    85: "Light snow showers",
    86: "Heavy snow showers",
    95: "Thunderstorm",
  }), []);

  const getWeatherIcon = (code) => {
    if (code === 0) return <IoMdSunny />;
    if (code >= 1 && code <= 3) return <IoMdCloudy />;
    if (code === 45 || code === 48) return <BsCloudHaze2Fill />;
    if (code >= 51 && code <= 57) return <BsCloudDrizzleFill />;
    if (code >= 61 && code <= 67) return <IoMdRainy />;
    if (code >= 71 && code <= 77) return <IoMdSnow />;
    if (code >= 80 && code <= 82) return <IoMdRainy />;
    if (code === 85 || code === 86) return <IoMdSnow />;
    if (code === 95) return <IoMdThunderstorm />;
    return <IoMdCloudy />;
  };

  const fetchWeather = async () => {
    if (!state.location) return;

    dispatch({ type: "FETCH_WEATHER_START" });

    try {
      const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${state.location}&count=1&language=en&format=json`;
      const geoResponse = await axios.get(geoUrl);

      if (geoResponse.data.results && geoResponse.data.results.length > 0) {
        const { latitude, longitude, country } = geoResponse.data.results[0];
        dispatch({ type: "SET_COUNTRY", payload: country });

        const weatherUrl = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,apparent_temperature,weathercode,visibility,relative_humidity_2m,windspeed_10m`;
        const weatherResponse = await axios.get(weatherUrl);

        if (weatherResponse.data.hourly) {
          dispatch({
            type: "FETCH_WEATHER_SUCCESS",
            payload: {
              temperature: weatherResponse.data.hourly.temperature_2m[0],
              feelsLike: weatherResponse.data.hourly.apparent_temperature[0],
              weatherCode: weatherResponse.data.hourly.weathercode[0],
              description:
                weatherDescriptions[weatherResponse.data.hourly.weathercode[0]] ||
                "No description",
              visibility: weatherResponse.data.hourly.visibility[0],
              humidity: weatherResponse.data.hourly.relative_humidity_2m[0],
              windSpeed: weatherResponse.data.hourly.windspeed_10m[0],
            },
          });
        } else {
          dispatch({
            type: "FETCH_WEATHER_ERROR",
            payload: "Weather data unavailable. Please try again.",
          });
        }
      } else {
        dispatch({
          type: "FETCH_WEATHER_ERROR",
          payload: "Location not found. Please enter a valid city or country.",
        });
      }
    } catch (error) {
      dispatch({
        type: "FETCH_WEATHER_ERROR",
        payload: "Failed to fetch data. Please try again.",
      });
    }
  };

  const currentDate = new Date();
  const day = currentDate.getDate();
  const month = currentDate.toLocaleString("default", { month: "long" });
  const year = currentDate.getFullYear();

  const handleSearchClick = (e) => {
    e.preventDefault();
    fetchWeather();
  };

  const handleLocationChange = (e) => {
    dispatch({ type: "SET_LOCATION", payload: e.target.value });
  };

  useEffect(() => {
    fetchWeather();
  }, []);

  return (
    <div className="w-full h-screen bg-gradientBg bg-no-repeat bg-cover bg-center flex flex-col items-center justify-center px-4 lg:px-0">
      {state.error && (
        <div className="w-full text-center bg-red-500 text-white py-3">
          {state.error}
        </div>
      )}

      <form
        className="h-16 bg-black/30 w-full max-w-[450px] rounded-full backdrop-blur-[32px] mb-8"
        onSubmit={handleSearchClick}
      >
        <div className="h-full relative flex items-center justify-between p-2">
          <input
            className="flex-1 bg-transparent outline-none placeholder:text-white text-[15px] font-light pl-6 h-full"
            type="text"
            placeholder="Search by city or country"
            value={state.location}
            onChange={handleLocationChange}
          />
          <button
            className="bg-[#1ab8ed] hover:bg-[#15abdd] w-20 h-12 rounded-full flex justify-center items-center transition"
            type="submit"
          >
            <IoMdSearch className="text-2xl text-white" />
          </button>
        </div>
      </form>

      <div>
        <div className="w-full max-w-[450px] bg-black/20 min-h-[584px] text-white backdrop-blur-[32px] rounded-[32px] py-12 px-6">
          {state.loading ? (
            <div className="w-full text-center">
              <div className="spinner-border text-white" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          ) : state.weatherData ? (
            <div className="flex items-start gap-x-5">
              <div className="text-[87px]">{getWeatherIcon(state.weatherData.weatherCode)}</div>
              <div className="text-xl mt-4">
                {month} {day}, {year}
              </div>
              <div className="text-2xl font-semibold">
                {state.location}, {state.country}
              </div>
            </div>
          ) : (
            <p>Enter a location to get weather information.</p>
          )}

          {state.weatherData && (
            <div className="my-20">
              <div className="flex justify-center items-center">
                <div className="text-[44px] leading-none font-light">
                  {state.weatherData.temperature}°C
                </div>
              </div>
              <div className="capitalize text-center">
                {state.weatherData.description}
              </div>
            </div>
          )}

          {state.weatherData && (
            <div className="max-w-[378px] mx-auto flex flex-col gap-y-6">
              <div className="flex justify-between">
                <div className="flex items-center gap-x-2">
                  <div className="text-[20px]">
                    <BsEye />
                  </div>
                  <div className="ml-2">Visibility: {state.weatherData.visibility / 1000} km</div>
                </div>
                <div className="flex items-center gap-x-2">
                  <div className="text-[20px]">
                    <BsThermometer />
                  </div>
                  <div className="ml-2">Feels like: {state.weatherData.feelsLike}°C</div>
                </div>
              </div>
              <div className="flex justify-between">
                <div className="flex items-center gap-x-2">
                  <div className="text-[20px]">
                    <BsDroplet />
                  </div>
                  <div className="ml-2">Humidity: {state.weatherData.humidity}%</div>
                </div>
                <div className="flex items-center gap-x-2">
                  <div className="text-[20px]">
                    <BsWind />
                  </div>
                  <div className="ml-2">Wind: {state.weatherData.windSpeed} km/h</div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
