import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  IoMdSunny,
  IoMdRainy,
  IoMdCloudy,
  IoMdSnow,
  IoMdThunderstorm,
  IoMdSearch,
} from "react-icons/io";
import { BsCloudHaze2Fill, BsCloudDrizzleFill, BsEye } from "react-icons/bs";

const App = () => {
  const [location, setLocation] = useState("Pune");
  const [loading, setLoading] = useState(false);
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [country, setCountry] = useState("");

  // Mapping of weather codes to descriptions
  const weatherDescriptions = {
    0: "Clear sky",
    1: "Mainly clear",
    2: "Partly cloudy",
    3: "Cloudy",
    45: "Fog",
    48: "Depositing rime fog",
    51: "Light drizzle",
    53: "Moderate drizzle",
    55: "Dense drizzle",
    56: "Light freezing drizzle",
    57: "Dense freezing drizzle",
    61: "Light rain",
    63: "Moderate rain",
    65: "Heavy rain",
    66: "Light freezing rain",
    67: "Heavy freezing rain",
    71: "Light snow",
    73: "Moderate snow",
    75: "Heavy snow",
    77: "Snow grains",
    80: "Showers of rain",
    81: "Heavy showers of rain",
    82: "Violent showers of rain",
    85: "Light snow showers",
    86: "Heavy snow showers",
    95: "Thunderstorm",
  };

  // Function to get the weather icon based on weather code
  const getWeatherIcon = (code) => {
    if (code === 0) return <IoMdSunny />;
    if (code >= 1 && code <= 3) return <IoMdCloudy />;
    if (code === 45 || code === 48) return <BsCloudHaze2Fill />;
    if (code >= 51 && code <= 57) return <BsCloudDrizzleFill />;
    if (code >= 61 && code <= 67) return <IoMdRainy />;
    if (code >= 71 && code <= 77) return <IoMdSnow />;
    if (code >= 80 && code <= 82) return <IoMdRainy />;
    if (code === 85 || code === 86) return <IoMdSnow />;
    if (code === 95) return <IoMdThunderstorm />;
    return <IoMdCloudy />; // Default icon
  };

  // Fetch coordinates and weather data based on location
  useEffect(() => {
    const fetchWeather = async () => {
      setLoading(true);
      setError(null);

      try {
        // Get coordinates from location name
        const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${location}&count=1&language=en&format=json`;
        const geoResponse = await axios.get(geoUrl);

        if (geoResponse.data.results && geoResponse.data.results.length > 0) {
          const { latitude, longitude, country } = geoResponse.data.results[0];
          setCountry(country); // Set the country state

          // Get weather data with weathercode and visibility
          const weatherUrl = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,weathercode,visibility`;
          const weatherResponse = await axios.get(weatherUrl);

          if (weatherResponse.data.hourly) {
            setWeatherData({
              temperature: weatherResponse.data.hourly.temperature_2m[0], // First hour's temperature
              weatherCode: weatherResponse.data.hourly.weathercode[0], // First hour's weather code
              description: weatherDescriptions[weatherResponse.data.hourly.weathercode[0]] || "No description", // Get description
              visibility: weatherResponse.data.hourly.visibility[0], // First hour's visibility
            });
          } else {
            setError("Weather data unavailable. Please try again.");
          }
        } else {
          setError("Location not found. Please enter a valid city or country.");
        }
      } catch (error) {
        setError("Failed to fetch data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, [location]);

  // Get current date, month, and year
  const currentDate = new Date();
  const day = currentDate.getDate();
  const month = currentDate.toLocaleString("default", { month: "long" });
  const year = currentDate.getFullYear();

  return (
    <div className="w-full h-screen bg-gradientBg bg-no-repeat bg-cover bg-center flex flex-col items-center justify-center px-4 lg:px-0">
      <form>Form</form>
      <div>
        <div className="w-full max-w-[450px] bg-black/20  min-h-[584px] text-white backdrop-blur-[32px] rounded[32px] py-12 px-6">
          <div className="search">
            <input
              type="text"
              placeholder="Enter city or country"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
            <button onClick={() => setLocation(location)}>
              <IoMdSearch />
            </button>
          </div>

          {error ? (
            <p className="error">{error}</p>
          ) : weatherData ? (
            <div className="flex items-start gap-x-5">
              <div className="text-[87px]">{getWeatherIcon(weatherData.weatherCode)}</div>

              {/* Display Current Date, Month, and Year */}
              <div className="text-xl mt-4">{month} {day}, {year}</div>
              <div className="text-2xl font-semibold">{location}, {country}</div>
            </div>
          ) : (
            <p>Enter a location to get weather information.</p>
          )}

          {/* Card Body */}
          <div className="my-20">
            <div className="flex justify-center items-center">
              <div className="text-[44px] leading-none font-light">
                Temperature: {weatherData.temperature}°C
              </div>
            </div>

            {/* Display Weather Description */}
            <div className="capitalize text-center">
              {weatherData.description}
            </div>
<div> <BsEye  /></div>
            {/* Display Visibility */}
            <div >

              Visibility:<span className='ml-2'>{weatherData.visibility/1000} km</span> 
            </div>
          </div>

          {/* Card Bottom */}
          <div>Card bottom</div>
        </div>
      </div>
    </div>
  );
};

export default App;
