import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  IoMdSunny,
  IoMdRainy,
  IoMdCloudy,
  IoMdSnow,
  IoMdThunderstorm,
  IoMdSearch,
} from "react-icons/io";
import { BsCloudHaze2Fill, BsCloudDrizzleFill } from "react-icons/bs";

const App = () => {
  const [location, setLocation] = useState("Pune");
  const [loading, setLoading] = useState(false);
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [country, setCountry] = useState("");

  // Function to get the weather icon based on weather code
  const getWeatherIcon = (code) => {
    if (code === 0) return <IoMdSunny />;
    if (code >= 1 && code <= 3) return <IoMdCloudy />;
    if (code === 45 || code === 48) return <BsCloudHaze2Fill />;
    if (code >= 51 && code <= 57) return <BsCloudDrizzleFill />;
    if (code >= 61 && code <= 67) return <IoMdRainy />;
    if (code >= 71 && code <= 77) return <IoMdSnow />;
    if (code >= 80 && code <= 82) return <IoMdRainy />;
    if (code === 85 || code === 86) return <IoMdSnow />;
    if (code === 95) return <IoMdThunderstorm />;
    return <IoMdCloudy />; // Default icon
  };

  // Fetch coordinates and weather data based on location
  useEffect(() => {
    const fetchWeather = async () => {
      setLoading(true);
      setError(null);

      try {
        // Get coordinates from location name
        const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${location}&count=1&language=en&format=json`;
        const geoResponse = await axios.get(geoUrl);

        if (geoResponse.data.results && geoResponse.data.results.length > 0) {
          const { latitude, longitude, country } = geoResponse.data.results[0];
          setCountry(country);  // Set the country state

          // Get weather data with weathercode
          const weatherUrl = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,weathercode`;
          const weatherResponse = await axios.get(weatherUrl);

          if (weatherResponse.data.hourly) {
            setWeatherData({
              temperature: weatherResponse.data.hourly.temperature_2m[0], // First hour's temperature
              weatherCode: weatherResponse.data.hourly.weathercode[0], // First hour's weather code
            });
          } else {
            setError("Weather data unavailable. Please try again.");
          }
        } else {
          setError("Location not found. Please enter a valid city or country.");
        }
      } catch (error) {
        setError("Failed to fetch data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, [location]);

  // Get current date, month, and year
  const currentDate = new Date();
  const day = currentDate.getDate();
  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();

  return (
    <div className="w-full h-screen bg-gradientBg bg-no-repeat bg-cover bg-center flex flex-col items-center justify-center px-4 lg:px-0">
      <form>Form</form>
      <div>
        <div
          className="w-full max-w-[450px] bg-black/20  min-h-[584px] text-white backdrop-blur-[32px] rounded[32px] py-12 px-6"
        >
          <div className="search">
            <input
              type="text"
              placeholder="Enter city or country"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
            <button onClick={() => setLocation(location)}>
              <IoMdSearch />
            </button>
          </div>

          {/* Display Current Date, Month, and Year */}
          <div className="text-xl mt-4">
            {month} {day}, {year}
          </div>

          {error ? (
            <p className="error">{error}</p>
          ) : weatherData ? (
            <div className="weather-info">
              <div className="text-2xl font-semibold">
                {location}, {country}
              </div>
              <p>Temperature: {weatherData.temperature}Â°C</p>
              <div className="text-[87px]">{getWeatherIcon(weatherData.weatherCode)}</div>
            </div>
          ) : (
            <p>Enter a location to get weather information.</p>
          )}

          {/* card top */}

          {/* card body */}
          <div>Card body</div>
          {/* card bottom */}
          <div>Card bottom</div>
        </div>
      </div>
    </div>
  );
};

export default App;
